document.addEventListener("DOMContentLoaded",function(){init_story_slider()});const init_story_slider=()=>{console.log("init story slider!");var r=$("#story_slider");r.slick({autoplay:!1,dots:!0,arrows:!0,infinite:!1}),r.on("beforeChange",function(n,o,i,t){var e=r.find(".slick-current video")[0];e.controls=!1,e&&e.pause()}),r.on("afterChange",function(n,o,i){var t=r.find(".slick-current video")[0];t.controls=!1,t&&(t.currentTime=0,t.play())})};