add_shortcode('post_featured_image','post_featured_image_fun');
function post_featured_image_fun()
{
	$post_id = get_the_ID();
	$embed = get_field('podcast');
	if($embed)
	{
		 return $embed;
	}
	else{
		
		return  get_the_post_thumbnail( $post_id, 'large' );    ;
	}
	
	
	
}



