add_shortcode('related_posts', 'read_detail_func');
function read_detail_func() {
    
      $a = shortcode_atts( array(
		 
		'limit' => '3',
	 
		 
	), $atts );


$post_id = get_the_ID();
	
	
	$args = array(
	'post_type' => 'post',
    'posts_per_page' => $a['limit'],
    'post__not_in' => array( $post_id ),
  
	
	'order'   => 'ASC',
	'orderby' => 'date',
 
	
    );
    
    
$the_query = new WP_Query( $args );


// The Loop
if ( $the_query->have_posts() ) {
	 
 	while ( $the_query->have_posts() ) {
		$the_query->the_post();

$post_id = get_the_ID();		
		
		$data .= '<div class="et_pb_column et_pb_column_1_3 et_pb_column_7  et_pb_css_mix_blend_mode_passthrough '.$a['mode'].'">
    <div class="et_pb_module et_pb_code  RWlpostbox">
       <div class="et_pb_code_inner">
        <div class="blgimg"><a href="'.get_the_permalink().'">
  '.get_the_post_thumbnail( $post_id, 'post-thumb-blog' ).'
</a></div>
<div class="blgcont">
  <p><span>'.get_the_time( 'm d Y' ).'</span></p> 
  <h4><a href="'.get_the_permalink().'">'.get_the_title().'</a></h4>
</div>
</div>
</div>
</div>';
		
 	}
 	
 	
 	
}
else
{
    
}

return $data;
	
/* Restore original Post Data */
wp_reset_postdata();		 
    
    
    
    

}



add_shortcode('all_posts', 'all_posts_fn');
function all_posts_fn() {
    
    
     $category = get_the_category();  
    
     if($category[0]->name)
     {
          $post_type = "post"; 
     }
    
    
      $a = shortcode_atts( array(
		 
		'limit' => '-1',
	 
		 
	), $atts );


$post_id = get_the_ID();
	
	
	$args = array(
    'post_type' => $post_type,
    'posts_per_page' => $a['limit'],
    
    'tax_query' => array(
		array(
			'taxonomy' => 'category',
			'field'    => 'name',
			'terms'    => $category[0]->name,
		),
 	),
     
  
	
	'order'   => 'ASC',
	'orderby' => 'date',
 
	
    );
    
    
$the_query = new WP_Query( $args );


// The Loop
if ( $the_query->have_posts() ) {
	 
 	while ( $the_query->have_posts() ) {
		$the_query->the_post();


$term_list = get_the_terms($post_id, 'category');
$types ='';
foreach($term_list as $term_single) {
     $types .= ucfirst($term_single->slug).', ';
}
$typesz = rtrim($types, ', ');		
		
		$data .= '<div class="et_pb_column et_pb_column_1_3 et_pb_column_7  et_pb_css_mix_blend_mode_passthrough '.$a['mode'].'">
    <div class="et_pb_module et_pb_code  RWlpostbox">
       <div class="et_pb_code_inner">
        <div class="blgimg"><a href="'.get_the_permalink().'">
  '.get_the_post_thumbnail( get_the_ID(), 'post-thumb-blog' ).'
</a></div>
<div class="blgcont">
  <p><span class="readbg bgyellow">'.$typesz.'</span><span>'.get_the_time( 'm d Y' ).'</span></p> 
  <h4><a href="'.get_the_permalink().'">'.get_the_title().'</a></h4>
</div>
</div>
</div>
</div>';
		
 	}
 	
 	
 	
}
else
{
    
}

return $data;
	
/* Restore original Post Data */
wp_reset_postdata();		 
    
    
     

}



