add_shortcode('all_posts_offerings', 'all_posts_offerings_fn');
function all_posts_offerings_fn() {
    
  
$term = get_queried_object();
$tax = '';
if($term->slug)
{
     $tax =  array(
		array(
			'taxonomy' => 'offering_categories',
			'field'    => 'slug',
			'terms'    => $term->slug,
		),
 	);
}
	
	
	$args = array(
    'post_type' => 'offering',
    'posts_per_page' => -1,
  
	
	'tax_query' =>$tax,
 	
 	
 	 	'meta_key'     => 'offering_date',
	'meta_value'   => date( "m/d/Y" ), // change to how "event date" is stored
	'meta_compare' => '>',
	
	'order'   => 'ASC',
	'orderby' => 'meta_value',
	'meta_type' => 'DATE'
 
	
    );
    
    
$the_query = new WP_Query( $args );


// The Loop
if ( $the_query->have_posts() ) {
	 
 	while ( $the_query->have_posts() ) {
		$the_query->the_post();


$post_id = get_the_ID();

$term_list = get_the_terms($post_id, 'offering_categories');
$types ='';
foreach($term_list as $term_single) {
     $types .= ucfirst($term_single->slug).'';
}
$typesz = rtrim($types, ', ');	


$event_date = get_field('offering_date', $post_id);
$sec = strtotime($event_date); 
		
	 $data .= '<div class="et_pb_column et_pb_column_1_3 et_pb_column_22 mar-r-0  et_pb_css_mix_blend_mode_passthrough">
    <div class="et_pb_module et_pb_code et_pb_code_2 RWlpostbox">
        <div class="et_pb_code_inner">
            <div class="blgimg"><a href="'.get_the_permalink().'">
            '.get_the_post_thumbnail( $post_id, 'post-thumb-blog' ).'
            </a></div>
        <div class="Blog_two">
        <div class="blodDate">
            <span class="pstdate">'.date("d", $sec).'</span>
            <span class="pstmon">'.date("M", $sec).'</span>
        </div>
        <div class="blgcont-two">
            <p><span class="readbg bg'.$types.'">'.$typesz.'</span></p> 
            <h4><a href="'.get_the_permalink().'">'.get_the_title().'</a></h4>
        </div>
         </div>
        </div>
    </div>
</div>';
		
 	}
 	
 	
 	
}
else
{
    $data = '<div class="noevent"><p>Hmmmm… looks like we don’t have any upcoming events of this type. Please check back soon.</p></div>';
}

return $data;
	
/* Restore original Post Data */
wp_reset_postdata();		 
    
    
     

}



