}

add_shortcode('post_category', 'post_category_fn');
function post_category_fn() {
    
     $id = get_the_ID();
    
    return get_the_category( $id )[0]->name;
    
}


add_shortcode('post_tax', 'post_tax_fn');
function post_tax_fn() {
  
 
    
$term_list = get_the_terms($post_id, 'offering_categories');
$types ='';
foreach($term_list as $term_single) {
     $types .= ucfirst($term_single->slug).', ';
}
$typesz = rtrim($types, ', ');
    
    
    return $typesz;
}

add_shortcode('previous_post', 'previous_post_fn');
function previous_post_fn() {
    
    
    $post_type = get_post_type();
	$category_obj = get_the_category();
    $category = $category_obj[0]->slug;
    
     /**
         *  Infinite next and previous post looping in WordPress
         */
        if( get_adjacent_post(false, '', true) ) {
           $link =  get_previous_post_link('%link', 'Previous', true );
        } else {
            $first = new WP_Query('post_type='.$post_type.'&posts_per_page=1&order=DESC&category='.$category); $first->the_post();
                $link = '<a href="' . get_permalink() . '">Previous</a>';
            wp_reset_query();
        };
    
    
    return $link;

    
}


add_shortcode('next_post', 'next_post_fn');
function next_post_fn() {
    
    
    $post_type = get_post_type();
	$category_obj = get_the_category();
    $category = $category_obj[0]->slug;
    
    if( get_adjacent_post(false, '', false) ) {
          $link =  get_next_post_link('%link', 'Next', true);
        } else {
            $last = new WP_Query('post_type='.$post_type.'&posts_per_page=1&order=ASC&category='.$category); $last->the_post();
                $link = '<a href="' . get_permalink() . '">Next</a>';
            wp_reset_query();
        };
        
    return $link;    

    
}






