function offerings_form_func( $atts ) {
	// Check rows existexists.
  if( have_rows('offer_testimonal') ):
	
	
	$slider .= '<div id="testimonal" class="testimonal nonloop owl-carousel">';

    // Loop through rows.
    while( have_rows('offer_testimonal') ) : the_row();

        // Load sub field value.
        $image = get_sub_field('author_img');
	    $attachment_image = wp_get_attachment_url($image);
	    $name = get_sub_field('author_name');
	    $description = get_sub_field('author_description');
	    $role = get_sub_field('author_role');
	
	$slider .= '<div class="item"><div class="offer-testimonal"><div class="autdes"><p>'.$description.'</p></div><div class="authorinfo"><div class="authorimg"><img src='.$attachment_image.'></div><div class="authcont"><h6>'.$name.'</h6>
<p>'.$role.'</p></div></div></div></div>';
	
	
	// End loop.
    endwhile;
	
	$slider .= '</div>';
	
	return $slider;

// No value.
else :
    // D
endif;
    }

