add_shortcode('team_slider', 'team_slider_func');
function team_slider_func()
{
    
 $args = array(
	'post_type' => 'teams',
	 
);   
    

// The Query
$the_query = new WP_Query( $args );

// The Loop
if ( $the_query->have_posts() ) {
	$slider = '<div class="owl-carousel teamslider owl-theme">';
	while ( $the_query->have_posts() ) {
		$the_query->the_post();
		
		$image_id = get_field('slider_photo');
		$attachment_image = wp_get_attachment_url($image_id);
		$slider .= '<div class="aboutteam"><div class="teamimg"><img src="'.$attachment_image.'" /></div><h3>' . get_the_title() . '</h3><p>'.get_field('short_bio').'</p><a class="plusico" href="'.get_the_permalink().'">+</a><a class="minusico" href="'.get_the_permalink().'">Read More</a></div>';
	}
	$slider .= '</div>';
} else {
	// no posts found
}
/* Restore original Post Data */
wp_reset_postdata();
    
      
    return $slider;
 
    
    
}

